import { useState, useContext} from "react";
import FormInput from "../form-input/form-input.component";
import Button from "../button/button.component";
import { UserContext } from "../../contexts/user.context";
import { creatUserDocumentFromAuth,signInWithGooglePopup, signInAuthUserWithEmailAndPassword } from "../../utils/firebase/firebase.utils";
import './sign-in-form.styles.scss'

const defaultFormFields= {
    email: '',
    password: '',
}

const SignInForm = () =>{
    const [formFields, setFormFields] = useState(defaultFormFields);
    const { email, password}= formFields;
    const {setCurrentUser} = useContext(UserContext)
    
    const resetFormFields = ()=>{
        setFormFields(defaultFormFields);
    }
    const signInWithGoogle = async ()=>{
        const {user} = await signInWithGooglePopup();
        setCurrentUser(user);
        await creatUserDocumentFromAuth(user);
    }

    const handleSubmit = async(event)=>{

        event.preventDefault();
        
        try {
            const {user} = await signInAuthUserWithEmailAndPassword(email ,password)
            setCurrentUser(user);
            resetFormFields()
        } catch (error) {
          switch(error.code){
            case 'auth/wrong-password': 
                alert('Password is incorrect');
                break;
            case 'auth/user-not-found': 
                alert('There is no user associated with this email');
                break;
            default:
                console.log(error);
            }
        }
            
    }

    const handleChange = (event) =>{
        const {name, value} = event.target;
        setFormFields({...formFields, [name]:value })
    }

    return(
        <div className="sign-up-container">
            <h2>Already have an account?</h2>
            <span>Sign up with your email and password</span>
            <form onSubmit={handleSubmit}>
                <label ></label>
                <FormInput label= 'Email' required type="email" onChange = {handleChange} name = "email" value={email} />

                <label ></label>
                <FormInput label= 'Password' required type="password" onChange = {handleChange} name = "password" value={password} />
                <div className="buttons-container">
                <Button type="submit">Sign In</Button>
                <Button type ='button' buttonType='google' onClick ={signInWithGoogle}>Google Sign In</Button>
                </div>
                
            </form>
        </div>
    )
}

export default SignInForm
